import java.time.LocalDate;
import java.time.temporal.ChronoUnit;

public class DateDifference {
    public static int getDaysDifference(String input) {
        LocalDate today = LocalDate.now();
        LocalDate targetDate = today;

        if (input.equals("今天")) {
            targetDate = today;
        } else if (input.equals("昨天")) {
            targetDate = today.minusDays(1);
        } else if (input.startsWith("最近")) {
            String[] parts = input.split(" ");
            int num = Integer.parseInt(parts[1]);
            if (parts[2].equals("周")) {
                targetDate = today.minusWeeks(num);
            } else if (parts[2].equals("天")) {
                targetDate = today.minusDays(num);
            } else if (parts[2].equals("月")) {
                targetDate = today.minusMonths(num);
            }
        }

        return (int) ChronoUnit.DAYS.between(targetDate, today.plusDays(1));
    }
}
