    public static boolean isValidJavaFileName(String fileName) {
        if (fileName == null || fileName.isEmpty()) {
            return false;
        }

        if (!fileName.endsWith(".java")) {
            return false;
        }

        String name = fileName.substring(0, fileName.length() - 5);
        if (name.length() < 1 || name.length() > 63) {
