public static double cosineSimilarity(double[] x, double[] y) {
    double dotProduct = 0.0;
    double normX = 0.0;
    double normY = 0.0;
    for (int i = 0; i < x.length; i++) {
        dotProduct += x[i] * y[i];
        normX += Math.pow(x[i], 2);
        normY += Math.pow(y[i], 2);
    }
    return dotProduct / (Math.sqrt(normX) * Math.sqrt(normY));
}
