public class LinearRegressionLoss {
    public static double calculateMSE(double[] actual, double[] predicted) {
        if (actual.length != predicted.length) {
            throw new IllegalArgumentException("The length of actual and predicted arrays must be the same.");
        }

        double sumOfSquares = 0.0;
        for (int i = 0; i < actual.length; i++) {
            double error = actual[i] - predicted[i];
            sumOfSquares += error * error;
        }

        return sumOfSquares / actual.length;
    }

    public static double calculateMAE(double[] actual, double[] predicted) {
        if (actual.length != predicted.length) {
            throw new IllegalArgumentException("The length of actual and predicted arrays must be the same.");
        }

        double sumOfAbsoluteErrors = 0.0;
        for (int i = 0; i < actual.length; i++) {
            double error = actual[i] - predicted[i];
            sumOfAbsoluteErrors += Math.abs(error);
        }

        return sumOfAbsoluteErrors / actual.length;
    }

    public static double calculateHuberLoss(double[] actual, double[] predicted, double delta) {
        if (actual.length != predicted.length) {
            throw new IllegalArgumentException("The length of actual and predicted arrays must be the same.");
        }

        double sumOfLosses = 0.0;
        for (int i = 0; i < actual.length; i++) {
            double error = actual[i] - predicted[i];
            if (Math.abs(error) <= delta) {
                sumOfLosses += 0.5 * error * error;
            } else {
                sumOfLosses += delta * (Math.abs(error) - 0.5 * delta);
            }
        }

        return sumOfLosses / actual.length;
    }

    public static void main(String[] args) {
        double[] actual = {3.0, 5.0, 7.0, 9.0, 11.0};
        double[] predicted = {2.5, 5.5, 7.5, 8.5, 10.5};

        System.out.println("Mean Squared Error: " + calculateMSE(actual, predicted));
        System.out.println("Mean Absolute Error: " + calculateMAE(actual, predicted));
        System.out.println("Huber Loss (delta = 1.0): " + calculateHuberLoss(actual, predicted, 1.0));
    }
}
```

