import java.util.Map;
import java.util.PriorityQueue;
import java.util.Queue;

public class Huffman {
    private String message;

    public void send(String message) {
        this.message = message;
        String encodedMessage = encode(message);
        System.out.println("Encoded message: " + encodedMessage);
    }

    public String recv() {
        return decode(message);
    }

    private String encode(String message) {
        // Create a Huffman tree
        HuffmanTree tree = new HuffmanTree(message);

        // Assign binary codes to each character
        Map<Character, String> codes = new HashMap<>();
        tree.assignCodes(codes, "");

        // Replace each character with its binary code
        StringBuilder encodedMessage = new StringBuilder();
        for (char c : message.toCharArray()) {
            encodedMessage.append(codes.get(c));
        }

        return encodedMessage.toString();
    }

    private String decode(String encodedMessage) {
        // Create a Huffman tree
        HuffmanTree tree = new HuffmanTree(message);

        // Decode the binary code
        StringBuilder decodedMessage = new StringBuilder();
        String code = "";
        for (char c : encodedMessage.toCharArray()) {
            code += c;
            Character character = tree.getCharacter(code);
            if (character != null) {
                decodedMessage.append(character);
                code = "";
            }
        }

        return decodedMessage.toString();
    }

    private static class HuffmanTree {
        private Node root;

        public HuffmanTree(String message) {
            // Create a priority queue of character frequencies
            Queue<Node> pq = new PriorityQueue<>((a, b) -> a.frequency - b.frequency);
            Map<Character, Integer> frequencies = new HashMap<>();
            for (char c : message.toCharArray()) {
                frequencies.merge(c, 1, Integer::sum);
            }
            for (Map.Entry<Character, Integer> entry : frequencies.entrySet()) {
                pq.add(new Node(entry.getKey(), entry.getValue()));
            }

            // Build the Huffman tree
            while (pq.size() > 1) {
                Node left = pq.poll();
                Node right = pq.poll();
                Node parent = new Node(null, left.frequency + right.frequency);
                parent.left = left;
                parent.right = right;
                pq.add(parent);
            }

            root = pq.poll();
        }

        public void assignCodes(Map<Character, String> codes, String code) {
            if (root.left == null && root.right == null) {
                codes.put(root.character, code);
            } else {
                if (root.left != null) {
                    root.left.assignCodes(codes, code + "0");
                }
                if (root.right != null) {
                    root.right.assignCodes(codes, code + "1");
                }
            }
        }

        public Character getCharacter(String code) {
            Node current = root;
            for (char c : code.toCharArray()) {
                if (c == '0' && current.left != null) {
                    current = current.left;
                } else if (c == '1' && current.right != null) {
                    current = current.right;
                } else {
                    return null;
                }
            }
            return current.character;
        }

        private static class Node implements Comparable<Node> {
            private Character character;
            private int frequency;
            private Node left;
            private Node right;

            public Node(Character character, int frequency) {
                this.character = character;
                this.frequency = frequency;
            }

            public void assignCodes(Map<Character, String> codes, String code) {
                if (left == null && right == null) {
                    codes.put(character, code);
                } else {
                    if (left != null) {
                        left.assignCodes(codes, code + "0");
                    }
                    if (right != null) {
                        right.assignCodes(codes, code + "1");
                    }
                }
            }

            public Character getCharacter(String code) {
                Node current = this;
                for (char c : code.toCharArray()) {
                    if (c == '0' && current.left != null) {
                        current = current.left;
                    } else if (c == '1' && current.right != null) {
                        current = current.right;
                    } else {
                        return null;
                    }
                }
                return current.character;
            }

            @Override
            public int compareTo(Node other) {
                return frequency - other.frequency;
            }
        }
    }
}
