 class MyString:
    def __init__(self, s):
        self.s = s

    def trim(self):
        return self.s.strip()

    def split(self, separator, N=-1):
        if N == -1:
            return self.s.split(separator)

        result = []
        start = 0

        for _ in range(N):
            end = self.s.find(separator, start)
            if end == -1:
                result.append(self.s[start:])
                break
            result.append(self.s[start:end])
            start = end + len(separator)

        return result

