import java.util.ArrayList;
import java.util.Date;

class Task {
    String name;
    Date dueDate;

    public Task(String name, Date dueDate) {
        this.name = name;
        this.dueDate = dueDate;
    }
}

class Model {
    ArrayList<Task> tasks = new ArrayList<>();

    public void addTask(String name, Date dueDate) {
        tasks.add(new Task(name, dueDate));
    }

    public void removeTask(int index) {
        tasks.remove(index);
    }

    public void displayTasks() {
        for (Task task : tasks) {
            System.out.println(task.name + " - " + task.dueDate);
        }
    }
}

class Controller {
    Model model;

    public Controller(Model model) {
        this.model = model;
    }

    public void addTask(String name, Date dueDate) {
        model.addTask(name, dueDate);
    }

    public void removeTask(int index) {
        model.removeTask(index);
    }

    public void displayTasks() {
        model.displayTasks();
    }
}

public class Main {
    public static void main(String[] args) {
        Model model = new Model();
        Controller controller = new Controller(model);

        controller.addTask("Task 1", new Date());
        controller.addTask("Task 2", new Date());
        controller.addTask("Task 3", new Date());

        controller.displayTasks();

        controller.removeTask(1);

        controller.displayTasks();
    }
}
```

