class Main {
    public static int[] calculateProportion(double p1, double p2, double p3, int total) {
        int[] result = new int[3];
        result[0] = (int) (p1 * total);
        result[1] = (int) (p2 * total);
        result[2] = (int) (p3 * total);

        int sum = result[0] + result[1] + result[2];
        if (sum == total) {
            return result;
        }

        // Adjust the result to match the total
        int[] adjustment = new int[3];
        if (sum > total) {
            adjustment[0] = result[0] - (sum - total);
            adjustment[1] = result[1] - (sum - total);
            adjustment[2] = result[2] - (sum - total);
        } else {
            adjustment[0] = result[0] + (total - sum);
            adjustment[1] = result[1] + (total - sum);
            adjustment[2] = result[2] + (total - sum);
        }

        // Check if the adjustment is valid
        if (adjustment[0] < 0 || adjustment[1] < 0 || adjustment[2] < 0) {
            return result;
        }

        // Apply the adjustment
        result[0] = adjustment[0];
        result[1] = adjustment[1];
        result[2] = adjustment[2];

