```java
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Comparator;
import java.util.HashMap;
import java.util.HashSet;
import java.util.List;
import java.util.Map;
import java.util.Set;
import java.util.TreeSet;

public class AlarmSystem {
    private Map<Integer, Alarm> alarms;

    public AlarmSystem() {
        alarms = new HashMap<>();
    }

    public boolean addAlarm(int id, Set<Integer> weekdays, int hour, int minute, int type) {
        if (alarms.containsKey(id)) {
            return false;
        }

        Alarm alarm = new Alarm(id, weekdays, hour, minute, type);
        alarms.put(id, alarm);
        return true;
    }

    public boolean deleteAlarm(int id) {
        if (alarms.containsKey(id)) {
            alarms.remove(id);
            return true;
        }
        return false;
    }

    public List<Alarm> queryAlarm(int weekday, int hour, int startMinute, int endMinute) {
        List<Alarm> result = new ArrayList<>();
        for (Alarm alarm : alarms.values()) {
            if (alarm.weekdays.contains(weekday) && alarm.hour == hour && alarm.minute >= startMinute && alarm.minute <= endMinute) {
                result.add(alarm);
            }
        }

        result.sort(Comparator.comparingInt(a -> a.minute).thenComparingInt(a -> a.type).thenComparingInt(a -> a.id));
        return result;
    }

    private static class Alarm {
        int id;
        Set<Integer> weekdays;
        int hour;
        int minute;
        int type;

        public Alarm(int id, Set<Integer> weekdays, int hour, int minute, int type) {
            this.id = id;
            this.weekdays = weekdays;
            this.hour = hour;
            this.minute = minute;
            this.type = type;
        }
    }
}
```

