import java.io.BufferedReader;
import java.io.File;
import java.io.FileReader;
import java.io.IOException;

public class FileEncoder {
    public static void encodeFile(File encodingFile, File encodedFile) throws IOException {
        BufferedReader reader = new BufferedReader(new FileReader(encodingFile));
        StringBuilder builder = new StringBuilder();
        int c;
        while ((c = reader.read()) != -1) {
            char ch = (char) c;
            if (Character.isDigit(ch)) {
                // Encrypt digits
                if (ch == '9') {
                    builder.append('0');
                } else {
                    builder.append((char) (ch + 1));
                }
            } else if (Character.isLetter(ch)) {
                // Encrypt letters
                if (ch == 'z' || ch == 'Z') {
                    builder.append('a');
                } else {
                    builder.append((char) (ch + 1));
                }
            } else {
                // Keep other characters as is
                builder.append(ch);
            }
        }
        reader.close();
        // Write the encrypted content to the output file
        encodedFile.createNewFile();
        BufferedWriter writer = new BufferedWriter(new FileWriter(encodedFile));
        writer.write(builder.toString());
        writer.close();
    }
}
```

