import java.util.PriorityQueue;

public class KthLargestInMatrix {
    public int findKthLargestValue(int n, int m, int k) {
        PriorityQueue<Integer> minHeap = new PriorityQueue<>();
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                minHeap.add(i * j);
                if (minHeap.size() > k) {
                    minHeap.poll();
                }
            }
        }
        return minHeap.peek();
    }
}
```

