import java.util.*;

public class Main {
    public static List<Integer> findDeadlocks(Map<Integer, Set<Integer>> holdMap, Map<Integer, Set<Integer>> waitMap) {
        Set<Integer> deadlocks = new HashSet<>();
        for (int pid : holdMap.keySet()) {
            if (isDeadlock(pid, holdMap, waitMap, new HashSet<>())) {
                deadlocks.add(pid);
            }
        }
        List<Integer> result = new ArrayList<>(deadlocks);
        Collections.sort(result);
        return result;
    }

    private static boolean isDeadlock(int pid, Map<Integer, Set<Integer>> holdMap, Map<Integer, Set<Integer>> waitMap, Set<Integer> visited) {
        if (visited.contains(pid)) {
            return true;
        }
        visited.add(pid);
        Set<Integer> waitResources = waitMap.get(pid);
        for (int resource : waitResources) {
            if (holdMap.containsKey(resource)) {
                for (int holder : holdMap.get(resource)) {
                    if (isDeadlock(holder, holdMap, waitMap, visited)) {
                        return true;
                    }
                }
            }
        }
        visited.remove(pid);
        return false;
    }
}
