 import math

# Euclidean distance
def euclidean_distance(point1, point2):
    return math.sqrt((point1[0] - point2[0])**2 + (point1[1] - point2[1])**2)

# Manhattan distance
def manhattan_distance(point1, point2):
    return abs(point1[0] - point2[0]) + abs(point1[1] - point2[1])

# Chebyshev distance
def chebyshev_distance(point1, point2):
    return max(abs(point1[0] - point2[0]), abs(point1[1] - point2[1]))

# Minkowski distance
def minkowski_distance(point1, point2, p):
    return ((abs(point1[0] - point2[0])**p + abs(point1[1] - point2[1])**p)**(1/p))

# Cosine similarity
def cosine_similarity(point1, point2):
    return (point1[0] * point2[0] + point1[1] * point2[1]) / (math.sqrt(point1[0]**2 + point1[1]**2) * math.sqrt(point2[0]**2 + point2[1]**2))

# Hamming distance
def hamming_distance(point1, point2):
    return sum(point1[i] != point2[i] for i in range(len(point1)))

# Jaccard similarity
def jaccard_similarity(point1, point2):
    intersection = len(set(point1).intersection(point2))
    union = len(set(point1).union(point2))
    return intersection / union

