```java
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

public class MarkdownToHTMLTest {

    @Test
    void testParseMarkdown1() {
        String markdown = "# Header 1\nThis is a paragraph.\n## Header 2\n* List item 1\n* List item 2";
        String expected = "<h1>Header 1</h1>\n<p>This is a paragraph.</p>\n<h2>Header 2</h2>\n<ul>\n<li>List item 1</li>\n<li>List item 2</li>\n</ul>\n";
        assertEquals(expected, MarkdownToHTML.parseMarkdown(markdown));
    }
}

class MarkdownToHTML {
    public static String parseMarkdown(String markdown) {
        StringBuilder html = new StringBuilder();
        String[] lines = markdown.split("\n");
        boolean inList = false;
        for (String line : lines) {
            if (line.startsWith("# ")) {
                html.append("<h1>").append(line.substring(2)).append("</h1>\n");
            } else if (line.startsWith("## ")) {
                html.append("<h2>").append(line.substring(3)).append("</h2>\n");
            } else if (line.startsWith("* ")) {
                if (!inList) {
                    html.append("<ul>\n");
                    inList = true;
                }
                html.append("<li>").append(line.substring(2)).append("</li>\n");
            } else {
                if (inList) {
                    html.append("</ul>\n");
                    inList = false;
                }
                html.append("<p>").append(line).append("</p>\n");
            }
        }
        if (inList) {
            html.append("</ul>\n");
        }
        return html.toString();
    }
}
```

