def count_html_tags(html):
    tag_count = 0
    max_nested_depth = 0
    current_depth = 0

    for i in range(len(html)):
        if html[i] == '<':
            tag_count += 1
            current_depth += 1
            if current_depth > max_nested_depth:
                max_nested_depth = current_depth
        elif html[i] == '>':
            current_depth -= 1

    return tag_count, max_nested_depth
