import xml.etree.ElementTree as ET

def parse_xml(file_path: str, tag_name: str) -> List[str]:

    try:
        root = ET.parse(file_path).getroot()
    except ET.ParseError:
        return []
    data_list = []
    for element in root.findall(tag_name):
        data_list.append(element.text)
    return data_list

