def is_valid_sequence(s):
    # 判断字符串长度是否在 2 到 10 之间
    if len(s) < 2 or len(s) > 10:
        return False

        # 判断字符串中的字符是否都是小写字母
    for char in s:
        if not char.islower():
            return False

            # 判断字符串中是否出现连续的重复字符
    for i in range(len(s) - 1):
        if s[i] == s[i + 1]:
            return False

            # 判断左右两部分是否都是回文字符串或递增序列
    left, right = s[:len(s) // 2], s[len(s) // 2:]
    if left == right[::-1] or sorted(left) == sorted(right):
        return True
    else:
        return False
