import shutil
import os
import threading
import time


def copy_folder(source_folder, dest_folders):
    for dest_folder in dest_folders:
        try:
            if os.path.exists(dest_folder):
                shutil.rmtree(dest_folder)
            shutil.copytree(source_folder, dest_folder)
            print("success from {source_folder} to {dest_folder}")
        except Exception as e:
            print("copy {source_folder} to {dest_folder} is err:{e}")
def create_thread(source_folder, dest_folders):
    # 计算每个线程需要拷贝的目标文件夹数量
    threads_num = len(dest_folders)
    folders_per_thread = len(dest_folders) // threads_num

    threads = []
    for i in range(threads_num):
        start = i * folders_per_thread
        end = start + folders_per_thread
        if i == threads_num - 1:
            end = len(dest_folders)
        thread = threading.Thread(target=copy_folder, args=(source_folder, dest_folders[start:end]))
        threads.append(thread)

    return threads

def run(threads):
    # 记录开始时间
    start_time = time.time()
    # 启动所有线程
    for thread in threads:
        thread.start()
    # 等待所有线程结束
    for thread in threads:
        thread.join()
    # 记录结束时间并输出用时
    end_time = time.time()
    print(f"time: {end_time - start_time} 秒")


def multithread_copy(source_folder, dest_folders):
    threads = create_thread(source_folder, dest_folders)
    run(threads)

