import math

def euclidean_distance(point1, point2):
    return math.sqrt(sum((p - q) ** 2 for p, q in zip(point1, point2)))

def manhattan_distance(point1, point2):
    return sum(abs(p - q) for p, q in zip(point1, point2))

def chebyshev_distance(point1, point2):
    return max(abs(p - q) for p, q in zip(point1, point2))

def minkowski_distance(point1, point2, p):
    return math.pow(sum(abs(p - q) ** p for p, q in zip(point1, point2)), 1/p)

def cosine_similarity(vector1, vector2):
    dot_product = sum(p * q for p, q in zip(vector1, vector2))
    norm_vector1 = math.sqrt(sum(p ** 2 for p in vector1))
    norm_vector2 = math.sqrt(sum(q ** 2 for q in vector2))
    return dot_product / (norm_vector1 * norm_vector2)


def hamming_distance(string1, string2):
    return sum(p != q for p, q in zip(string1, string2))

def jaccard_similarity(set1, set2):
    intersection_size = len(set1.intersection(set2))
    union_size = len(set1.union(set2))
    return intersection_size / union_size




